import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
// import { ReactDOM } from "react";


function App() {
  return (
    <div className="container">
      <Header />
      <Catalog />
      <Footer />
    </div>
  );
}

function Item(props) {
  return (
    <div className="item">
      <img src={props.url} alt={props.name}></img>{" "}
      <div>
        <h3>{props.name}</h3>
        <span>{props.price+".00"}</span>
      </div>
    </div>
  );
}

function Header() {
  return (
    <header className="header">
      <h1>God's Grace Distributions</h1>
    </header>
  );
}
function Catalog() {
  return (
    <main className="catalog">
      <h2>Our Catalog</h2>
      <Item
        name="Notebook"
        price="80"
        url="https://godsgraceproducts.in/wp-content/uploads/2023/08/IMG-6034-removebg-preview.png"
      />
      <Item
        name="Folder"
        price="1199"
        url="https://godsgraceproducts.in/wp-content/uploads/2023/09/CF-1367-All-1.jpg"
      />
    </main>
  );
}
function Footer() {
  const hour = new Date().getHours();
  const openHour = 10;
  const closeHour = 18;
  //   if (hour > openHour && hour <= closeHour) {
  //     alert("We're open!");
  //   } else {
  //     alert("Sorry we missed you!");
  //   }
  // const isOpen
  return (
    <div className="footer">
      <footer>We're currently open!</footer>
    </div>
  );
}

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);
